import React, { Component } from 'react';

class Counter extends Component {   
state={
    count:2
}

    render() { 
        return (
        <React.Fragment>
            
            <span> {this.formatCount()}</span>
            <button >Increment</button>

        </React.Fragment>);
    }

    formatCount(){

        const { counter } =this.state;

        return count === 0 ? "Zero":counter;
    }
}

 export default Counter;
