//using Class library
import React, { Component } from "react";

//component-ClassJSX
class Countera extends Component {
  //No.1 ======= Les champs propriétés de la classes dans État. ==========================
  state = {
    imageUrl: "http://localhost/photos/200.jpg",
    count: 0,
    tags: ["tag1", "tag2", "tag3"]
  };
  //No.2 ===========Special delivery - that report to Dom.=========================
  // method-1
  render() {
    //those objects are reporting to Dom
    return (
      <div>
        <img
          src={this.state.imageUrl}
          alt="JloSmile"
          height="15%"
          width="15%"
        />
        <hr />

        {/* Here Start Home Video school after JloSmile */}

        {this.state.tags.length === 0 && "Please Create a new Tag&&&"}
        {this.rendTags()}

        <span className={this.getBadgeClasses()}> {this.formatCount()}</span>
        <button className="btn  btn-m btn-secondary">Btn to Increment</button>
      </div>
    );
  }

  //No.3 ============= Some methods==========================
  //this.method-2
  rendTags() {
    if (this.state.tags.length === 0)
      return <p>There are no tags to report!</p>;

    return (
      <ul>
        {" "}
        {this.state.tags.map(tag => (
          <li key={tag}> {tag} </li>
        ))}{" "}
      </ul>
    );
  } //end of this.methos-2
  //method-2
  formatCount() {
    return "zero";
  }

  //method-3
  getBadgeClasses() {
    let classes = "badge m-2 badge-";
    classes += this.state.count === 0 ? "warning" : "primary";
    return classes;
  }
  //create provisory style back-CSS
  styles = {
    fontSize: 40,
    fontWeight: "bold",
    color: "pink"
  };
} //end class

//Finally class available to use(importable class in index.js).
export default Countera;
