//imrc tab
import React, { Component } from "react";
import "./sheet.css";
import Countero from "./Counter1";
//import 201.jpg from './Photos'

//ClassJSX-extends component : cc tab
class Counters extends Component {

  
  //state-declaration and props locally
  //The component that owns a piece of the state,
  //should be the one modify it.
  state = {
    img1Src: "http://localhost/photos/200.jpg",
    prodCounterItems: [
      { id: 1, value: 4},
      { id: 2, value: 0 },
      { id: 3, value: 0 },
      { id: 4, value: 0 }
    ]
  };


  //---------------------- functions ------------------------------------

  //create rest function
  handleReset = () => {
    //console.log('reset');
    const prodCounterItems = this.state.prodCounterItems.map(v => {
      v.value = 0;
      return v;
    });
    this.setState({ prodCounterItems });
  };

  handleIncrement = item => {
    //  console.log(item);
    //clone-state
    const prodCounterItems = [...this.state.prodCounterItems];
    //find item_index
    const itemDex = prodCounterItems.indexOf(item);
     //clone-item
    prodCounterItems[itemDex]={ ...item };  
    prodCounterItems[itemDex].value++;

   // console.log(this.state.prodCounterItems[index]);
   this.setState({prodCounterItems});
};

  //create Delete function
  handleDelete = itemId => {
    //  console.log('Event Handler Call', itemId);
    // const counterItems = this.state.prodCounterItems.filter(c => c.id !== itemId);
    // this.setState({prodCounterItems:counterItems});
    const prodCounterItems = this.state.prodCounterItems.filter(
      f => f.id !== itemId
    );
    this.setState({ prodCounterItems });
  };

  //create recapitulation method
  rendRecaps() {
    return (
      <ul>
        <li> {"Recap # 2: Composing Components - Mosh Hamedani "}</li>
        <ol>
          <li> {"Pass Data"}</li>
          <li> {"Debug React Developer Tools ext. "}</li>
          <li> {"Props vs State "}</li>
          <li> {"Raise-props-onDelete and handle Events Delete  "}</li>
          <li> {"Removing the local state"}</li>
          <li> {"Multiple Components in Sync "}</li>
          <li> {"Lifting state up "}</li>
         
          <li> {"Functional Components "}</li>

          <li> {"Lifecycle Hooks "}</li>
        </ol>
      </ul>
    );
  } //end of rendRecaps

   //create recapitulation method
 recaps() {
    return (
      <ul>
         <li> {"Recap # 1: Class and state "}</li>
          <ol>
              <li> {'JSX9(ClassJSX-extends component) - Rendering Lists: {this.state.tags.map(tag => (<li key={tag}> {tag} </li> ))}'}</li>
              <li> {'Conditional Rendering: color_badge += this.state.count === 0 ? "warning" : "primary";'}</li>
              <li> {"Handling Events: onClick={this.handleBtnIncrement}"}</li>
              <li> {"Updating the state: this.setState({ count: this.state.count += 1 });"}</li>
          </ol>
      </ul>
    );
  } //end of rendRecaps
  
} //end class
//must export to work
export default Counters;
