import React, { Component } from 'react';

class Counter extends Component {   
state={
    countera:0,
    imageUrl:'http://localhost/photos/200.jpg' 
}
    styles={
        fontWeight: 'bold',
        fontSize: '10px',
        color:'Red'
        

 }


    render() { 
        return (
        <div>
            
            <img src={this.state.imageUrl} alt="" />

            <span style={this.state.style} className="badge badge-primary m-3"> {this.formatCount()}</span>

            <button className="btn btn-secondary btn-m" >Increment</button>

        </div>);
    }

    formatCount(){

        const  {countera} =this.state;
        const  xx  = <h3>Love</h3>;

        return countera === 0 ? xx:countera;
    }
}

 export default Counter;
