//using Class library
import React, { Component } from "react";

//component-ClassJSX
class Countera extends Component {
  //No.1 ======= Les champs propriétés de la classes dans État. ==========================
  state = {
    imageUrl: "http://localhost/photos/200.jpg",
    count: 0,
    tags: ["tag1", "tag2", "tag3"]
  };
  //No.2 ======Special delivery method - that report to the Dom.=========================
  // necessary method-1
  render() {
    //delivery may take some fields here that will be reporting to the Dom.

    //report:
    return (
      <div>
        <img
          src={this.state.imageUrl}
          alt="JloSmile"
          height="15%"
          width="15%"
        />
        <hr />

        {/* Here Start Home Video school after JloSmile */}

        {this.state.tags.length === 0 && "Please Create a new Tag&&&"}
        {this.rendTags()}

        <span className={this.colorBadgeClasses()}> {this.formatCount()}</span>
        <button onClick={this.handlerBtn} className="btn  btn-m btn-secondary">Btn to Increment</button>
      </div>
    ); //end report
  } //end delivery report

  //No.3 ============= Some other methods==========================
  //create this.method-2 to map and report list_tags
  rendTags() {
    if (this.state.tags.length === 0)
      return <p>There are no tags to report!</p>;

    return (
      <ul>
        {" "}
        {this.state.tags.map(tag => (
          <li key={tag}> {tag} </li>
        ))}{" "}
      </ul>
    );
  } //end of this.methos-2
  //method-2
  formatCount() {
    return "zero";
  }

  //method-3 condition to get effect color on badgeText
  colorBadgeClasses() {
    let color_badge = "badge m-2 badge-";
    color_badge += this.state.count === 0 ? "warning" : "primary";
    return color_badge;
  }
  //create provisory style back-CSS
  styles = {
    fontSize: 40,
    fontWeight: "bold",
    color: "pink"
  };//end local class-styles
} //end class

//Finally class available to use(importable class in index.js).
export default Countera;
