import React, { Component } from 'react';

class Counter extends Component {   
    //No.1: props
    state={
        love:'Love',
        dix:10,
        imageUrl:'http://localhost/photos/200.jpg' ,
        count:0
      
    };
        //No.2:livrer au Dom.
        // method-1 
        render() { 
            return (
            <div>
                <img src={this.state.imageUrl} alt="" />
                <span style={this.styles}  className="badge badge-primary m-2"> {this.state.love}</span>
                <button style={{fontSize:30}} className="btn btn-secondary btn-m" >{this.state.dix}</button>
                <br/>
               
                <span className="badge badge-primary m-2"> {this.formatCount()}</span>
                <button className="btn btn-secondary btn-m" >Btn to Increment</button>




            </div>
            );

        }
//No.3
        //method-2
        formatCount(){

         return   <h1> 'thisCallMeToReturn4U'</h1>
        }

        //provisory css
        styles = {
            fontSize: 40,
            fontWeight: "bold",
            color:"pink"
            
        };
}

 export default Counter;
