//imrc tab
import React, { Component } from 'react';
import "./sheet.css";
import Countero from './Counter1'


//ClassJSX-extends component : cc tab
class Counters extends Component {
    state = { img1Src: "http://localhost/photos/200.jpg" 
    }
    //delivery
    render() { 
        //report the import class 
        //or whatever you want below the return call any function:
        return (
           <div className="boxit">       
                <img src={this.state.img1Src}
                className="boxit" 
                alt="JlovSmile"
                height="15%"
                width="15%"
                /> 
                
                {this.rendRecaps()}  
                    <Countero />
                    <Countero />
                    <Countero />
                    <Countero />

            </div>  
        );//end report
    } // end delivery

//---------------------- functions ------------------------------------
    rendRecaps() {
  
        return (
          <ul>
            <li> {"Recap # 2: Composing Components "}</li>
            <ol>
            <li> {'Pass Data'}</li>
            <li> {'Raise and handle Events '}</li>
            <li> {"Multiple Components in Sync "}</li>
            <li> {"Funtional Components "}</li>
            <li> {"Lifecycle Hooks "}</li>
            </ol>
    
          </ul>
        );
    } //end of rendRecaps
}//end class
//must export to work
export default Counters;
