import React, { Component } from 'react';

class Counter extends Component {   
    state={
        love:'Love',
        countera:0,
        imageUrl:'http://localhost/photos/200.jpg' 
    };
        styles = {
            fontSize: 65,
            fontWeight: "bold",
            color:"pink"
            
        };


        
        render() { 
            return (
            <div>
                
                <img src={this.state.imageUrl} alt="" />

                <span style={this.styles}  className="badge badge-primary m-2"> {this.state.love}</span>

                <button style={{fontSize:40}} className="btn btn-secondary btn-m" >{this.state.countera}</button>
               
               <br/>
               
                <span className="badge badge-primary m-2"> {this.formatCount()}</span>

                <button className="btn btn-secondary btn-m" >Increment</button>



            </div>

             



            );

        }

        formatCount(){

            const  {countera} =this.state;
            const  xx  = <h3>Love</h3>;

            return countera === 0 ? xx:countera;
        }
}

 export default Counter;
